-- Listening Gap-Filling Assets
-- Generated from media files
-- Course ID: 4

-- Clear existing listening assets
DELETE FROM public.assets WHERE lessonid IN (301, 302, 303, 304, 305, 306, 307, 308, 309, 310, 311, 312, 313, 314, 315);

-- Insert listening assets

-- Reset sequence for assets table
SELECT setval('public.assets_id_seq', (SELECT COALESCE(MAX(id), 0) FROM public.assets) + 1, false);

-- Verify listening assets
SELECT 
  l.title as lesson,
  COUNT(a.id) as assets,
  COUNT(CASE WHEN a.assettype = 'audio' THEN 1 END) as audio_files,
  COUNT(CASE WHEN a.assettype = 'document' AND a.title LIKE '%handout%' THEN 1 END) as pdf_files,
  COUNT(CASE WHEN a.assettype = 'document' AND a.title LIKE '%answer%' THEN 1 END) as docx_files
FROM public.lessons l
LEFT JOIN public.assets a ON l.id = a.lessonid
WHERE l.courseid = 4
GROUP BY l.id, l.title
ORDER BY l.id;
